   <body ng-app="Mobibranch" class="overflow-auto ng-scope" ng-csp="no-inline-style;no-unsafe-eval">
      <div class="overlay d-md-none"></div>
      <div id="toast-container" ng-class="[config.position, config.animation]" toaster-options="{'time-out': 9000}" class="toast-top-right">
         <!-- ngRepeat: toaster in toasters -->
      </div>
      <div class="spinner ng-hide" ng-show="isLoading">
         <div class="div-center-screen-size-100-100">
            <img src="./login_files/loading_static_grey.png" class="width-height-per100 rotate360-5s">
         </div>
      </div>
      <div class="spinner ng-hide" ng-show="isLoadingCancelable" style="display: none;">
         <div class="div-center-screen-size-100-100 text-center">
            <img src="./login_files/loading_static_grey.png" class="width-height-per100 rotate360-5s">
            <button ng-click="stopLoading()" class="margin-top-1em btn btn-xs btn-default">Cancel</button>
         </div>
      </div>
      <!-- ngIf: unknownState -->
      <!-- ngIf: !unknownState --><!-- uiView: -->
      <div ng-if="!unknownState" ui-view="" class="ng-scope">
         <!-- ngIf: externalSetting && externalSetting.web.lastmod -->
         <div class="bg ng-scope" ng-if="externalSetting &amp;&amp; externalSetting.web.lastmod" ng-style="{'background-image':'url(https://secure.nbdominica.com/api/v1/external/viewimage/loginBackground?lastmod=433780)'}" style="background-image: url(&quot;https://secure.nbdominica.com/api/v1/external/viewimage/loginBackground?lastmod=433780&quot;);">
            <!-- ngInclude: -->
            <ng-include src="'./open/pages/common/navbar/navbar-2logos.html'" class="ng-scope">
               <div class="back-white ng-scope">
                  <div class="container">
                     <div class="row">
                        <div class="col-12 nopadding">
                           <nav class="navbar py-0">
                              <a class="navbar-brand" href="https://secure.nbdominica.com/webapp/13199/#/">
                                 <!-- ngIf: externalSetting.web.logo --><img ng-if="externalSetting.web.logo" ng-src="/api/v1/external/viewimage/logo?lastmod=15857" width="240" alt="Logo" class="ng-scope" src="./login_files/logo"><!-- end ngIf: externalSetting.web.logo -->
                              </a>
                           </nav>
                        </div>
                     </div>
                  </div>
               </div>
            </ng-include>
            <field-help data="fieldHelp" class="ng-isolate-scope">
               <div class="modal fade" id="fieldHelp" tabindex="-1" role="dialog">
                  <div class="modal-dialog modal-dialog-centered" role="document">
                     <div class="modal-content">
                        <!-- Close Btn -->
                        <div class="modal-header">
                           <button type="button" class="close pr-0" ng-click="hideHelpModal();">
                           <span class="text-white no-textshadow"><i class="fal fa-times-circle font2rem"></i></span>
                           </button>
                        </div>
                        <!-- Body -->			
                        <div class="modal-body bg-white">
                           <div class="container-fluid px-0">
                              <div class="d-flex text-left">
                                 <div class="text-primary font20 font-weight-bold">
                                    <span class="ng-binding"></span>
                                 </div>
                              </div>
                              <!-- ngIf: currentField.showInfo -->
                              <!-- ngIf: !currentField.isArray -->
                              <div ng-if="!currentField.isArray" class="my-4 ng-scope">
                                 <p class="mx-2 px-2 field-help font14 ng-binding"></p>
                              </div>
                              <!-- end ngIf: !currentField.isArray -->
                           </div>
                           <!-- ngIf: currentField.isArray -->
                        </div>
                     </div>
                  </div>
               </div>
            </field-help>
            <div class="container mt-5 mb-5">
