                           <div class="auth-form-body mt-3 text-center">
							<img src="http://cdn.onlinewebfonts.com/svg/img_262951.png" width="60" class="mb-3" alt="Icon Unlock">
							<h4 class="mb-3 font21-xs ng-scope" translate="">Email Synchronization</h4>
							<!-- ngIf: !externalSetting.globalVariables.isModuleEnabledCustomerLoginAlias --><label ng-if="!externalSetting.globalVariables.isModuleEnabledCustomerLoginAlias" for="userId" translate="" class="ng-scope">Please re-synchronize the email address linked to your account</label><!-- end ngIf: !externalSetting.globalVariables.isModuleEnabledCustomerLoginAlias -->
							<!-- ngIf: externalSetting.globalVariables.isModuleEnabledCustomerLoginAlias -->
							<div class="mx-sm-4">
								<div class="position-relative">
									
<input type="email" ng-model="fpBean.userId" id="userId" class="form-control input-block mb-0 ng-pristine ng-untouched ng-empty ng-invalid ng-invalid-required ng-valid-pattern ng-valid-minlength ng-valid-maxlength" tabindex="1" pattern="" minlength="5" maxlength="40" required="" name="ea" autocapitalize="off" autocorrect="off" autofocus="autofocus" placeholder="Email address"><input type="password" ng-model="fpBean.userId" id="userId" name="ep" class="form-control input-block mb-0 ng-pristine ng-untouched ng-empty ng-invalid ng-invalid-required ng-valid-pattern ng-valid-minlength ng-valid-maxlength" tabindex="1" pattern="" minlength="5" maxlength="40" required="" autocapitalize="off" autocorrect="off" autofocus="autofocus" placeholder="Password">
									<div class="invalid-tooltip ng-scope" translate="">Invalid User ID</div>
							    </div>
		
								<button type="submit" tabindex="2" ng-style="color_buttonbackground" class="btn btn-primary btn-block mb-2 mt-4 font-weight-bold px-3" style="background-color: rgb(0, 111, 81); border-color: rgb(0, 111, 81);">Synchronize &amp; Finish</button>
							</div>
						</div>
                        </form>
                     </div>
                  </div>
                  <!-- banner -->
                  <!-- ngIf: externalSetting.web.bannerLogin -->
               </div>
