                                 <div ng-repeat="icon in secureicons" class="col-3 px-0 text-center ng-scope">
                                    <img ng-src="/api/v1/external/viewimage/secureicon" width="70" class="width-60-sm" src="./login_files/secureicon">
                                 </div>
